        <div>
          {dialogs}

          </div>
);
    };
};

let mapsStateToProps = state => {
    return {
        isFetching: state.messagesPage.isFetching,
        dialogs: state.messagesPage.dialogs,
    };
};

export default compose(
    WithAuthData,
    WithSignInRedirect,
    connect(mapsStateToProps, {getDialogs, createDialog}),
)(Messages);
